/*Creación de DB*/
DROP DATABASE IF EXISTS DHToys_db;
CREATE DATABASE DHToys_db CHARACTER SET utf8 COLLATE utf8_general_ci;
USE DHToys_db;

/*Creación de Tablas*/

CREATE TABLE categories_users (
	id INT AUTO_INCREMENT NOT NULL,
	name VARCHAR(5) NOT NULL,
	PRIMARY KEY (id)
);

CREATE TABLE users (
	id INT AUTO_INCREMENT NOT NULL,
	name VARCHAR(255) NOT NULL,
	lastName VARCHAR(255) NOT NULL,
	email VARCHAR(255) NOT NULL,
	password VARCHAR(255) NOT NULL,
	avatar VARCHAR(255) NOT NULL,
    idCategoryUser INT,
    status BOOLEAN,
	PRIMARY KEY (id),
    FOREIGN KEY (idCategoryUser)
		REFERENCES categories_users(id)
);

CREATE TABLE categories_products (
	id INT AUTO_INCREMENT NOT NULL,
    name VARCHAR(255) NOT NULL,
    PRIMARY KEY (id)
);

CREATE TABLE products (
	id INT AUTO_INCREMENT NOT NULL,
    name VARCHAR(255) NOT NULL,
    description TEXT,
    image VARCHAR(255),
    price FLOAT,
    age INT,
    idCategoryProduct INT,
    status BOOLEAN,
    PRIMARY KEY (id),
    FOREIGN KEY (idCategoryProduct)
		REFERENCES categories_products(id)    
);

CREATE TABLE products_users (
	id INT AUTO_INCREMENT NOT NULL,
    idUser INT,
    idProduct INT,
    PRIMARY KEY (id),
    FOREIGN KEY (idUser) REFERENCES users(id),
    FOREIGN KEY (idProduct) REFERENCES products(id)
);


